alias theme-dark="cp -f ~/.dotfiles/alacritty/alacritty-nord.yml ~/.dotfiles/alacritty/alacritty.yml && cp -f ~/.dotfiles/git/gitconfig-dark ~/.dotfiles/git/gitconfig"
